SELECT T.CD_ATENDIMENTO,
       T.NM_PACIENTE,
       (SELECT SUM(R1.VL_TOTAL_CONTA) VLR FROM DBAMV.REG_FAT R1
       WHERE R1.CD_ATENDIMENTO = T.CD_ATENDIMENTO
       AND   R1.CD_CONVENIO = 414) VALOR_CONTA,
       SUM(T.N1)NIVEL1,
       SUM(T.N2)NIVEL2,
       SUM(T.N3)NIVEL3

 FROM
(
SELECT 
      RF1.CD_ATENDIMENTO,
      PAC.NM_PACIENTE,

      CASE WHEN IRF1.CD_PRO_FAT = '00017090' THEN SUM(IRF1.QT_LANCAMENTO) ELSE 0 END N1,
        
      CASE WHEN IRF1.CD_PRO_FAT = '00017091' THEN SUM(IRF1.QT_LANCAMENTO) ELSE 0 END N2,
        
      CASE WHEN IRF1.CD_PRO_FAT = '00017092' THEN SUM(IRF1.QT_LANCAMENTO) ELSE 0 END N3

FROM 
DBAMV.REG_FAT RF1,
DBAMV.ITREG_FAT IRF1,
DBAMV.REMESSA_FATURA REM1,
DBAMV.ATENDIME ATEND,
DBAMV.PACIENTE PAC

WHERE RF1.CD_REMESSA = REM1.CD_REMESSA
AND ATEND.CD_ATENDIMENTO = RF1.CD_ATENDIMENTO
AND ATEND.CD_PACIENTE = PAC.CD_PACIENTE
AND IRF1.CD_REG_fAT = RF1.CD_REG_FAT
AND IRF1.SN_PERTENCE_PACOTE = 'N'
AND IRF1.CD_PRO_FAT IN ('00017090', '00017091', '00017092','00000001','00000004')
AND RF1.CD_CONVENIO = 414
