select a.CD_ATENDIMENTO,
       a.TP_ATENDIMENTO,
       a.CD_CONVENIO,
       c.nm_convenio,
       p.nr_cpf,
       nf.nm_cliente,
       p.nm_paciente,
       p.dt_nascimento,
       p.ds_endereco,
       p.nr_endereco,
       p.cd_cidade,
       p.cd_banco,
       p.nr_cep,
       p.nr_fone,
       p.email,
       nvl(ra.cd_reg_amb,rf.cd_reg_fat) conta,
       cnv.cd_convenio,
       cnv.nm_convenio,
       cr.cd_con_rec,
       cr.nr_documento,
       cr.dt_emissao,
       cr.cd_nota_fiscal,
       nf.nr_id_nota_fiscal,
       nf.dt_emissao,
       icr.dt_vencimento,
       nvl(ra.vl_total_conta,rf.vl_total_conta) vlr_atendimento,
       nvl(icr.vl_soma_recebido,0)vl_soma_recebido,
      -- cr.vl_recebido,
    case when nvl(ra.vl_total_conta,rf.vl_total_conta) = nvl(icr.vl_soma_recebido,0) then 0
           when nvl(icr.vl_soma_recebido,0) = 0 then nvl(ra.vl_total_conta,rf.vl_total_conta)
           when nvl(ra.vl_total_conta,rf.vl_total_conta) <> nvl(icr.vl_soma_recebido,0)then  nvl((nvl(ra.vl_total_conta,rf.vl_total_conta) - icr.vl_soma_recebido),0)
           end saldo_devedor        
     
